import React, { useState, useEffect } from "react";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../firebase";
import { FaCalendarAlt, FaClock, FaMapMarkerAlt, FaMoneyBillAlt } from "react-icons/fa";
import { useNavigate, useLocation } from "react-router-dom";

const EventInfoCard = ({ id }) => {
  const [event, setEvent] = useState(null);
  const [numberOfBooking, setNumberOfBooking] = useState(1);
  const navigate = useNavigate();
  const location = useLocation(); // ใช้ useLocation เพื่อดึงข้อมูลจาก state

  // ดึง startDate และ endDate จาก location.state
  const { startDate, endDate } = location.state || {}; // ถ้าไม่มี state จะเป็น undefined

  // ดึงข้อมูลจาก Firestore โดยใช้ id
  useEffect(() => {
    const fetchEvent = async () => {
      if (!id) return;
      const docRef = doc(db, "event", id);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        setEvent(docSnap.data());
      } else {
        console.error("No such document!");
      }
    };

    fetchEvent();
  }, [id]);

  // Loading message
  if (!event) {
    return <p>Loading...</p>;
  }

  // เพิ่ม-ลดการจอง
  const handleIncrement = () => setNumberOfBooking((prev) => prev + 1);
  const handleDecrement = () => numberOfBooking > 1 && setNumberOfBooking((prev) => prev - 1);

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate(`/Reservation`, {
      state: { numberOfBooking },
    });
  };

  // ฟังก์ชันจัดการเวลา
  const formatEventTime = (eventTimePeriod) => {
    if (!eventTimePeriod) return "";

    // ตั้งค่าเวลาเริ่มต้นให้ไม่ต่ำกว่า 9:00 AM (540 นาที)
    const adjustedStartTime = Math.max(eventTimePeriod, 540); // 540 นาที = 9:00 AM
    const startHour = Math.floor(adjustedStartTime / 60);
    const startMinute = adjustedStartTime % 60;

    const endHour = Math.floor((adjustedStartTime + 90) / 60); // เพิ่ม 90 นาที
    const endMinute = (adjustedStartTime + 90) % 60;

    const formatTime = (h, m) => `${h}:${m.toString().padStart(2, "0")}`;
    return `${formatTime(startHour, startMinute)} - ${formatTime(endHour, endMinute)}`;
  };


  return (
    <div className="bg-white rounded-lg shadow-lg p-6 w-[353px] h-fit">
      {/* Display start and end date */}
      {startDate && endDate && (
        <div className="flex items-center mb-4">
          <FaCalendarAlt className="w-[17px] h-[17px] text-lightblack mr-2" />
          <p className="paragraph2 text-lightblack">
            {`From: ${startDate} To: ${endDate}`}
          </p>
        </div>
      )}

      {/* Date */}
      <div className="flex items-center mb-4">
        <FaCalendarAlt className="w-[17px] h-[17px] text-lightblack mr-2" />
        <p className="paragraph2 text-lightblack">{event.date || "No date available"}</p>
      </div>

      {/* Time */}
      <div className="flex items-center mb-4">
        <FaClock className="w-[17px] h-[17px] text-lightblack mr-2" />
        <p className="paragraph2 text-lightblack">
          {event.eventTimePeriod
            ? formatEventTime(event.eventTimePeriod)
            : "No time available"}
        </p>
      </div>

      {/* Location */}
      <div className="flex items-center mb-4">
        <FaMapMarkerAlt className="w-[17px] h-[17px] text-lightblack mr-2" />
        <p className="paragraph2 text-lightblack">
        {[
          event.villageNameEN,
          event.tamNameEN,
          event.ampNameEN,
          event.provNameEN,
          ]
        .filter(Boolean)
        .join(", ") || "No location available"
        }
        </p>
      </div>


      {/* Fee */}
      <div className="flex items-center mb-4">
        <FaMoneyBillAlt className="w-[17px] h-[17px] text-lightblack mr-2" />
        <p className="paragraph2 text-lightblack">
          {event.eventFee ? `${event.eventFee} ฿` : "Free"}
        </p>
      </div>

      {/* Booking Controls */}
      <div className="flex items-center justify-between mt-6 mb-4 pl-6">
        <button
          className="rounded-full border-lightgray border heading3 text-lightgray w-10 h-10 flex items-center justify-center"
          onClick={handleDecrement}
        >
          -
        </button>
        <span className="heading2 text-lightblack">{numberOfBooking}</span>
        <button
          className="rounded-full border-lightgray border heading3 text-lightgray w-10 h-10 flex items-center justify-center"
          onClick={handleIncrement}
        >
          +
        </button>
      </div>

      {/* Reserve Button */}
      <button
        className="bg-primary text-white py-3 w-full rounded-[30px] search"
        onClick={handleSubmit}
      >
        Reserve
      </button>
    </div>
  );
};

export default EventInfoCard;
